import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UserManagementComponent = () => {
  const [users, setUsers] = useState([]);
  const [vessels, setVessels] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [selectedVessels, setSelectedVessels] = useState([]);
  const [role, setRole] = useState('');

  useEffect(() => {
    fetchUsers();
    fetchVessels();
  }, []);

  const fetchUsers = async () => {
    const token = localStorage.getItem('authToken');
    const response = await axios.get('http://localhost:5001/api/users', {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    setUsers(response.data);
  };

  const fetchVessels = async () => {
    const token = localStorage.getItem('authToken');
    const response = await axios.get('http://localhost:5001/api/vessels', {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    setVessels(response.data);
  };

  const handleSelectUser = (user) => {
    setSelectedUser(user);
    setSelectedVessels(user.assignedVessels || []);
    setRole(user.role);
  };

  const handleVesselChange = (e) => {
    const selectedOptions = Array.from(e.target.selectedOptions, (option) => option.value);
    setSelectedVessels(selectedOptions);
  };

  const handleSave = async () => {
    const token = localStorage.getItem('authToken');
    await axios.put(`http://localhost:5001/api/users/${selectedUser._id}/vessels`, {
      vessels: selectedVessels,
    }, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    alert('Vessels assigned successfully');
    fetchUsers();
    setSelectedUser(null);
  };

  return (
    <div>
      <h1>User Management</h1>
      <table>
        <thead>
          <tr>
            <th>Username</th>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Email</th>
            <th>Role</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user._id}>
              <td>{user.username}</td>
              <td>{user.firstName}</td>
              <td>{user.lastName}</td>
              <td>{user.email}</td>
              <td>{user.role}</td>
              <td>
                <button onClick={() => handleSelectUser(user)}>Assign Vessels</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      {selectedUser && (
        <div>
          <h2>Assign Vessels to {selectedUser.username}</h2>
          <select
            multiple
            value={selectedVessels}
            onChange={handleVesselChange}
            disabled={role === 'Captain' || role === 'Crew'}
          >
            {vessels.map((vessel) => (
              <option key={vessel._id} value={vessel._id}>
                {vessel.name}
              </option>
            ))}
          </select>
          <button onClick={handleSave}>Save</button>
        </div>
      )}
    </div>
  );
};

export default UserManagementComponent;
